import logo from './logo.svg';
import './App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      quote: '',
      author: ''
    }

    this.getNewQuote = this.getNewQuote.bind(this);
  }


  getNewQuote(){

    const indx = Math.floor(Math.random() * quotes.length)

    this.setState({

      quote: quotes[indx].quote,
      author: quotes[indx].source
    })

  }

  render(){ 
    return (
      <div id="quote-box">

        <div id="author">{this.state.author}</div>

        <blockquote id="text">{this.state.quote}</blockquote>

        <a href="twitter.com/intent/tweet" id="tweet-quote" target="_blank">Tweet this quote</a>

        <button id="new-quote" onClick={this.getNewQuote()}>New Quote</button>

      </div>
    )
  }
}


export default App;
